-- create "tracks" table
CREATE TABLE `tracks` (`Name` nvarchar NOT NULL, `AlbumId` integer NULL, `MediaTypeId` integer NOT NULL, `GenreId` integer NULL, `Composer` nvarchar NULL, `Milliseconds` integer NOT NULL, `Bytes` integer NULL, `UnitPrice` numeric NOT NULL, `TrackId` integer NOT NULL, PRIMARY KEY (`TrackId`), CONSTRAINT `0` FOREIGN KEY (`MediaTypeId`) REFERENCES `media_types` (`MediaTypeId`) ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT `1` FOREIGN KEY (`GenreId`) REFERENCES `genres` (`GenreId`) ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT `2` FOREIGN KEY (`AlbumId`) REFERENCES `albums` (`AlbumId`) ON UPDATE NO ACTION ON DELETE NO ACTION);
